    }

    saveProduct(product) {
        return axios.post(PRODUCT_API_BASE_URL, product);
    }

    deleteEmployee(id) {
        return axios.delete(EMPLOYEE_API_BASE_URL + "/" + id);
      }
    
      getEmployeeById(id) {
